const fs = require('fs');
const http = require('http');

function uploadFile(FIXTURE, TEST_PORT) {
  const fileStream = fs.createReadStream(FIXTURE);
  const boundary = '----WebKitFormBoundary' + Date.now().toString(16);

  const headers = {
    'Content-Type': `multipart/form-data; boundary=${boundary}`,
  };

  const request = http.request(
    {
      host: 'localhost',
      port: TEST_PORT,
      path: '/upload',
      method: 'POST',
      headers,
    },
    (response) => {
      response.on('data', (data) => {});
    }
  );

  request.on('error', (err) => {
    console.error(err);
  });

  const bodyParts = [];

  const fileName = FIXTURE.split('/').pop();
  bodyParts.push(`--${boundary}\r\n`);
  bodyParts.push(`Content-Disposition: form-data; name="file"; filename="${fileName}"\r\n`);
  bodyParts.push('Content-Type: application/octet-stream\r\n\r\n');

  fileStream.pipe(
    new http.Writable({
      write(chunk, encoding, callback) {
        bodyParts.push(chunk);
        callback();
      },
      final(callback) {
        bodyParts.push('\r\n');
        bodyParts.push(`--${boundary}--\r\n`);

        const bodyBuffer = Buffer.concat(bodyParts);
        request.setHeader('Content-Length', bodyBuffer.length);
        request.write(bodyBuffer);
        request.end();
        callback();
      },
    })
  );
}
