const http = require('http');
const fs = require('fs');

function createVideoServer() {
  const server = http.createServer((req, res) => {
    const videoPath = 'assets/NASA.mp4';
    const stat = fs.statSync(videoPath);
    const fileSize = stat.size;
    const range = req.headers.range;
    if (!range) {
      res.statusMessage = 'Requested Range Not Satisfiable';
      return res.writeHead(416, { 'Content-Range': `bytes */${fileSize}` }).end();
    }

    const parts = range.replace(/bytes=/, '').split('-');
    const start = parseInt(parts[0], 10);
    const end = parts[1] ? parseInt(parts[1], 10) : fileSize - 1;
    const chunkSize = end - start + 1;

    res.writeHead(206, {
      'Content-Range': `bytes ${start}-${end}/${fileSize}`,
      'Accept-Ranges': 'bytes',
      'Content-Length': chunkSize,
      'Content-Type': 'video/mp4'
    });

    const videoStream = fs.createReadStream(videoPath, { start, end });
    videoStream.pipe(res);
  });

  server.listen(3000, '127.0.0.1', () => {
    console.log('Video server running at http://127.0.0.1:3000/');
  });
}
