const http = require('http');
const fs = require('fs');
const path = require('path');
const { exec } = require('child_process');

function createServer() {
  const server = http.createServer((req, res) => {
    if (req.url === '/favicon.ico') {
      res.writeHead(404);
      return res.end();
    }

    const htmlTemplatePath = path.join(__dirname, 'template.html');
    const outputPdfPath = path.join(__dirname, 'output.pdf');

    fs.readFile(htmlTemplatePath, 'utf8', (err, data) => {
      if (err) {
        res.writeHead(500);
        return res.end('Internal Server Error');
      }

      const htmlContent = data.replace('{{imagePath}}', path.join(__dirname, 'example.jpg'));

      const puppeteerLaunchCommand = `puppeteer --no-sandbox --disable-gpu --headless new --print-to-pdf=${outputPdfPath}`;
      const command = `${puppeteerLaunchCommand} -- ${JSON.stringify({ html: htmlContent })}`;

      exec(command, (error, stdout, stderr) => {
        if (error || stderr) {
          res.writeHead(500);
          return res.end('PDF generation failed');
        }

        res.writeHead(200, {
          'Content-Type': 'application/pdf',
          'Content-Disposition': 'inline; filename="output.pdf"'
        });

        const readableStream = fs.createReadStream(outputPdfPath);
        readableStream.pipe(res);
      });
    });
  });

  server.listen(8080, () => {
    console.log(`Server running at http://localhost:8080/`);
  });
}
