class Server {
  constructor(options) {
    this.options = Object.assign(
      {
        livereloadPath: '/livereload.js',
        livereloadFile: 'livereload.js',
        port: 3000,
      },
      options
    );
    this.clients = {};
    this.server = require('http').createServer();
    this.app = null;
    this.port = this.options.port;
  }

  configure(app) {
    this.app = app;
    if (this.app && this.app.use) {
      this.app.use(this.handler.bind(this));
    }
    return this;
  }

  handler(req, res, next) {
    if (!this.app || !this.app.handle) {
      this.directHandle(req, res);
      return;
    }
    const origEnd = res.end;
    res.end = function (chunk, encoding) {
      origEnd.call(res, chunk, encoding);
      if (req.url === this.options.livereloadPath) {
        this.livereload(req, res);
      }
    }.bind(this);
    this.app.handle(req, res, next);
  }

  directHandle(req, res) {
    if (req.url === this.options.livereloadPath) {
      this.livereload(req, res);
      return;
    }
    res.writeHead(404);
    res.end('Not Found');
  }

  livereload(req, res) {
    const fs = require('fs');
    const path = require('path');
    const filePath = path.resolve(__dirname, this.options.livereloadFile);
    fs.createReadStream(filePath).pipe(res);
  }

  listen(port, fn) {
    this.port = port || this.port;
    this.server.listen(this.port, () => {
      console.log(`Server listening on port ${this.port}`);
      if (fn) fn();
    });
    this.server.on('request', this.handler.bind(this));
    this.server.on('error', this.error.bind(this));
  }

  close(req, res) {
    this.server.close(() => {
      console.log('Server closed');
      if (res) res.end();
    });
  }

  error(e) {
    console.error('Server error:', e);
  }
}
