function createServer() {
  const server = http.createServer((req, res) => {
    const logDir = path.join(os.tmpdir(), 'http_logs');
    if (!fs.existsSync(logDir)) {
      fs.mkdirSync(logDir, { recursive: true });
    }

    const logFile = path.join(logDir, `request_${Date.now()}-${Math.random().toString(36).substr(2, 9)}.txt`);
    const writeStream = fs.createWriteStream(logFile);

    req.pipe(writeStream);

    console.log(`New request received. Logging to ${logFile}`);

    if (req.method === 'POST' && req.headers['content-type'] === 'application/x-www-form-urlencoded') {
      let body = '';
      req.on('data', chunk => {
        body += chunk.toString();
      });

      req.on('end', () => {
        console.log(`Received POST data: ${body}`);
        res.writeHead(200, { 'Content-Type': 'text/plain' });
        res.end('Data received and logged.\n');
      });
    } else if (req.url === '/upload') {
      res.writeHead(200, { 'Content-Type': 'text/html' });
      res.end(`
        <form action="/upload" method="post" enctype="multipart/form-data">
          <input type="file" name="file"><br>
          <input type="submit">
        </form>
      `);
    } else {
      res.writeHead(200, { 'Content-Type': 'text/plain' });
      res.end('Request logged.\n');
    }
  });

  server.on('connection', (socket) => {
    console.log('New connection established.');
    socket.on('close', () => {
      console.log('Connection closed.');
    });
  });

  server.listen(PORT, () => {
    console.log(`Server is running on port ${PORT}`);
  });
