        try {
            const response = await axios.post(provider.url, { date: currentDate });
            const $ = cheerio.load(response.data);

            $('div.channel').each((index, element) => {
                const lang = 'it';
                const name = $(element).find('h3.name').text().trim();
                const number = $(element).find('span.number').text().trim();
