                if (name && number && slug) {
                    allChannels.push({
                        lang,
                        name,
                        site_id: `${number}-${slug}`
                    });
                }
            });
        } catch (error) {
            console.error(`Error fetching data from ${provider.name}:`, error.message);
        }
    }

    return _.uniqBy(allChannels, 'site_id');
