const http = require('http');
const fs = require('fs');
const path = require('path');
const send = require('send');
const marked = require('marked');

class Server {
  constructor(config) {
    this.config = config || {};
    this._http = null;
    this.sockets = [];
    this.path = this.config.path || process.cwd();
  }

  updateServer() {
    const self = this;

    this._http = http.createServer((req, res) => {
      const requestPath = req.url === '/' ? 'index.html' : req.url;
      const fullPath = path.join(this.path, requestPath);

      if (req.method !== 'GET') {
        res.writeHead(405, { 'Content-Type': 'text/plain' });
        return res.end('Method Not Allowed');
      }

      if (fs.existsSync(fullPath) && fs.lstatSync(fullPath).isDirectory()) {
        const indexPath = path.join(fullPath, 'index.html');
        if (fs.existsSync(indexPath)) {
          return send(req, 'index.html', { root: fullPath }).pipe(res);
        } else {
          fs.readdir(fullPath, (err, files) => {
            if (err) {
              res.writeHead(500, { 'Content-Type': 'text/plain' });
              return res.end('Internal Server Error');
            }
            let html = '<ul>';
            files.forEach(file => {
              html += `<li><a href="${encodeURIComponent(file)}">${file}</a></li>`;
            });
            html += '</ul>';
            res.writeHead(200, { 'Content-Type': 'text/html' });
            res.end(html);
          });
        }
      } else {
        send(req, requestPath, { root: this.path })
          .on('error', err => {
            res.writeHead(404, { 'Content-Type': 'text/plain' });
            res.end('File Not Found');
          })
          .pipe(res);
      }

      req.on('close', () => {
        const index = self.sockets.indexOf(req.socket);
        if (index !== -1) {
          self.sockets.splice(index, 1);
        }
      });

      self.sockets.push(req.socket);
    });
  }

  start(callback) {
    this.updateServer();
    this._http.listen(this.config.port || 3000, callback);
  }

  stop(callback) {
    if (!this._http) return callback();

    this._http.close(() => {
      this.sockets.forEach(socket => socket.destroy());
      this.sockets = [];
      callback();
    });
  }
}
