const net = require('net');
const crypto = require('crypto');

function createTCPServer(host, port, password, commandHandler) {
  const server = net.createServer();

  server.on('listening', () => {
    console.log(`Server is listening on ${host}:${port}`);
  });

  server.on('connection', (socket) => {
    socket.setEncoding('utf8');
    let receive = { data: Buffer.alloc(0), socket };

    socket.on('data', (data) => {
      receiveData(receive, data, password, commandHandler);
    });

    socket.on('end', () => {
      console.log('Client disconnected');
    });

    socket.on('error', (err) => {
      console.error('Socket error:', err.message);
    });
  });

  server.on('error', (err) => {
    console.error('Server error:', err.message);
  });

  server.listen(port, host);
  return server;
}

function receiveData(receive, data, password, commandHandler) {
  receive.data = Buffer.concat([receive.data, data]);
  checkData(receive, password, commandHandler);
}

async function checkData(receive, password, commandHandler) {
  while (true) {
    if (receive.data.length < 12) break;

    const length = receive.data.readUInt32BE(0);
    const codeLength = receive.data.readUInt32BE(4);
    const dataLength = receive.data.readUInt32BE(8);

    if (receive.data.length < 12 + codeLength + dataLength) break;

    const code = receive.data.subarray(12, 12 + codeLength);
    const payload = receive.data.subarray(12 + codeLength, 12 + codeLength + dataLength);

    const valid = checkCode(payload, password, code);
    if (!valid) {
      receive.socket.write(pack({ code: Buffer.from([0x01]) }));
      receive.data = receive.data.subarray(12 + codeLength + dataLength);
      continue;
    }

    try {
      const result = await commandHandler(payload, code);
      receive.socket.write(pack(result));
    } catch (err) {
      receive.socket.write(pack({ code: Buffer.from([0x02]) }));
    }

    receive.data = receive.data.subarray(12 + codeLength + dataLength);
  }
}

function checkCode(data, password, code) {
  const timestamp = data.readBigUInt64BE(0);
  const cmd = data.subarray(8);
  const expectedCode = crypto.createHash('md5').update(`${timestamp.toString()}${password}${cmd.toString()}`).digest();
  return code.equals(expectedCode);
}

function pack({ code, data }) {
  const buffer = Buffer.alloc(12 + code.length + (data ? data.length : 0));
  buffer.writeUInt32BE(buffer.length - 12, 0);
  buffer.writeUInt32BE(code.length, 4);
  buffer.writeUInt32BE(data ? data.length : 0, 8);
  code.copy(buffer, 12);
  if (data) data.copy(buffer, 12 + code.length);
  return buffer;
}
