const net = require('net');

function createTCPServerProxy() {
  let server;
  let port;
  let dataReceived = '';
  let hasBeenCalled = false;

  const initialResponse = 'HTTP/1.1 200 OK\r\n\r\n';
  const subsequentResponse = 'HTTP/1.1 200 OK\r\ncontent-type: text/plain\r\ncontent-length: 5\r\n\r\nderp\n';

  function start(callback) {
    server = net.createServer((socket) => {
      hasBeenCalled = true;
      let firstResponseSent = false;

      socket.on('data', (data) => {
        dataReceived += data.toString();
        if (!firstResponseSent) {
          socket.write(initialResponse);
          firstResponseSent = true;
        } else {
          socket.write(subsequentResponse);
        }
      });

      socket.on('end', () => {
        socket.end();
      });
    });

    server.listen(0, () => {
      port = server.address().port;
      callback();
    });
  }

  function stop(callback) {
    server.close(() => {
      callback();
    });
  }

  function getData() {
    return dataReceived;
  }

  function wasCalled() {
    return hasBeenCalled;
  }

  return { start, stop, getData, wasCalled };
}
