        var seed = crypto.randomBytes(8).toString('hex');
        var fn = 'node-red-git-askpass-'+seed+'-sock';
        var listenPath;
        if (process.platform === 'win32') {
            listenPath = '\\\\.\\pipe\\'+fn;
        } else {
