                    var m = data.indexOf("\n");
                    if (m !== -1) {
                        parts.push(data.substring(0, m));
                        data = data.substring(m);
                        var line = parts.join("");
                        parts = [];
                        if (this.serverType === 'git') {
                            if (line === 'Username') {
                                connection.end(this.auth.username);
                            } else if (line === 'Password') {
                                connection.end(this.auth.password);
                                server.close();
                            }
                        } else if (this.serverType === 'ssh') {
                            if (line === 'The') {
                                connection.end('yes');
                            } else if (line === 'Enter') {
                                connection.end(this.auth.passphrase);
                            }
                        }
                    }
                    if (data.length > 0) {
                        parts.push(data);
                    }
                });
