    const crypto = require('crypto');
    const randomStr = crypto.randomBytes(8).toString('hex');

    if (os.platform() === 'win32') {
      return `\\\\.\\pipe\\git-cred-helper-${randomStr}`;
    } else {
      return path.join(os.tmpdir(), `git-cred-helper-${randomStr}`);
