      const net = require('net');
      this.server = net.createServer((socket) => {
        socket.on('data', (data) => {
          const request = data.toString().trim();
          let response;

          if (request.startsWith('get')) {
            response = [
              `username=${this.auth.username}`,
              `password=${this.auth.password || ''}`,
              `passphrase=${this.auth.passphrase || ''}`
