const net = require('net');
const tls = require('tls');
const EventEmitter = require('events');

class POP3Server extends EventEmitter {
  constructor(options = {}) {
    super();
    this.options = options;
    this.connections = new Set();
    this.server = this._createServer();
    this._setListeners();
  }

  _createServer() {
    return this.options.secure
      ? new tls.TLSServer(this.options.tlsOptions || {}, (socket) => {
          this.connect(socket, { ...this.options, secure: true });
        })
      : new net.Server((socket) => {
          this.connect(socket, { ...this.options, secure: false });
        });
  }

  _setListeners() {
    this.server.on('listening', this._onListening.bind(this));
    this.server.on('close', this._onClose.bind(this));
    this.server.on('error', this._onError.bind(this));
  }

  _onListening() {
    this.emit('listening');
  }

  _onClose() {
    this.emit('close');
  }

  _onError(err) {
    this.emit('error', err);
  }

  connect(socket, socketOptions) {
    const clientConnection = new POP3Client(socket, socketOptions);
    this.connections.add(clientConnection);

    clientConnection.on('end', () => {
      this.connections.delete(clientConnection);
    });

    clientConnection.on('error', (err) => {
      this.emit('clientError', err, clientConnection);
    });

    this.emit('connection', clientConnection);
  }

  listen(...args) {
    return this.server.listen(...args);
  }

  close(callback) {
    for (const conn of this.connections) {
      conn.socket.destroy();
    }
    this.server.close(callback);
  }
}

class POP3Client {
  constructor(socket, options) {
    this.socket = socket;
    this.options = options;
    this._setupSocketEvents();
  }

  _setupSocketEvents() {
    this.socket.on('data', (data) => {
      this.emit('data', data);
    });

    this.socket.on('end', () => {
      this.emit('end');
    });

    this.socket.on('error', (err) => {
      this.emit('error', err);
    });
  }

  send(data) {
    if (!this.socket.writable) return;
    this.socket.write(`${data}\r\n`);
  }

  on(event, listener) {
    return this.addListener(event, listener);
  }
}
