class Server {
  constructor(options, origins) {
    this.origins = origins || [];
    this.port = options?.port || 843;
    this.log = options?.log || console.log.bind(console);
    this.socket = null;
    this.server = options?.server || null;
    this.buffer = this.compile();
  }

  listen(port, server, cb) {
    this.port = port || this.port;
    this.server = server || this.server;

    this.socket = net.createServer((socket) => {
      socket.on('data', (data) => {
        if (data.toString().includes('<policy-file-request/>')) {
          this.responder(socket);
        } else {
          socket.destroy();
        }
      });

      socket.on('error', (err) => {
        this.log(`Socket error: ${err.message}`);
        socket.destroy();
      });
    });

    this.socket.listen(this.port, () => {
      this.log(`Flash policy server listening on port ${this.port}`);
      if (cb) cb();
    });

    this.socket.on('error', (err) => {
      this.log(`Server error: ${err.message}`);
    });

    return this;
  }

  responder(socket) {
    try {
      socket.write(
        Buffer.concat([
          Buffer.from('\0', 'binary'),
          this.buffer,
          Buffer.from('\0', 'binary')
        ])
      );
      socket.destroy();
    } catch (e) {
      this.log(`Error writing to socket: ${e.message}`);
      socket.destroy();
    }
  }

  compile() {
    let xml = '<?xml version="1.0"?>\n<!DOCTYPE cross-domain-policy SYSTEM "http://www.macromedia.com/xml/dtds/cross-domain-policy.dtd">\n<cross-domain-policy>\n';
    for (let origin of this.origins) {
      const [domain, port] = origin.split(':');
      xml += `  <allow-access-from domain="${domain}" port="${port}" secure="false"/>\n`;
    }
    xml += '</cross-domain-policy>';
    this.buffer = Buffer.from(xml, 'utf-8');
    return this;
  }

  add(...origins) {
    for (let origin of origins) {
      if (!this.origins.includes(origin)) {
        this.origins.push(origin);
      }
