class DNodeServer {
  constructor(cons, opts) {
    this.cons = cons;
    this.opts = opts || {};
    this.server = net.createServer();
    this.sessions = Object.create(null);
    this._setupWeakRefs();
    this._setupConnectionHandler();
  }

