      const sessions = new WeakMap();
      this._getSession = (id) => sessions.get(this.sessions[id]);
      this._setSession = (id, session) => sessions.set(this.sessions[id], session);
    } else {
      this._getSession = (id) => this.sessions[id];
      this._setSession = (id, session) => { this.sessions[id] = session; };
    }
  }

  _handleConnection(stream) {
    const id = this._randomId();
    const session = this.cons(stream, id);
    this._setSession(id, session);
