class P2PServer {
  constructor(send = () => {}) {
    this.tcpServer = net.createServer();
    this.clients = [];
    this.send = send;
    this.messageHandlers = {};

    this.tcpServer.on('connection', (socket) => {
      const jsonSocket = new JsonSocket(socket);
      this.clients.push(jsonSocket);

      jsonSocket.on('data', (message) => {
        if (message.type && this.messageHandlers[message.type]) {
          this.messageHandlers[message.type](message, jsonSocket);
        }
      });

      jsonSocket.on('close', () => {
        this.clients = this.clients.filter(client => client !== jsonSocket);
      });

      jsonSocket.on('error', (err) => {
        console.error('Client error:', err);
        this.clients = this.clients.filter(client => client !== jsonSocket);
      });
    });

    this.tcpServer.on('error', (err) => {
      console.error('Server error:', err);
    });
  }

  listen(port) {
    this.tcpServer.listen(port, '0.0.0.0');
  }

  close() {
    return new Promise((resolve) => {
      this.clients.forEach(client => client.destroy());
      this.tcpServer.close(() => resolve());
    });
  }

  on(type, callback) {
    this.messageHandlers[type] = callback;
  }
}
