            secureContext: this.secureContext.get('*'),
            isServer: true,
            server: this.server
        };

        let tlsSocket = new tls.TLSSocket(socket, socketOptions);
        tlsSocket.on('secure', () => {
            return callback(null, tlsSocket);
        });
