class IMAPServer {
  constructor(options) {
    const net = require('net');
    const tls = require('tls');

    this.options = options || {};
    this.server = net.createServer();
    this.connections = new Set();
    this._closeTimeout = null;

    this._setListeners();
  }

  connect(socket, socketOptions) {
    const connection = new (require('./IMAPConnection'))(socket, socketOptions);
    this.connections.add(connection);

    connection.on('close', () => {
      this.connections.delete(connection);
    });
  }

  listen(...args) {
    this.server.listen(...args);
  }

  close(callback) {
    if (this._closeTimeout) clearTimeout(this._closeTimeout);
    this._closeTimeout = setTimeout(() => {
      this.server.close((err) => {
        if (callback) callback(err);
      });
    }, 1000);
  }

  _setListeners() {
    this.server.on('listening', this._onListening.bind(this));
    this.server.on('close', this._onClose.bind(this));
    this.server.on('error', this._onError.bind(this));
    this.server.on('connection', this._handleProxy.bind(this));
  }

  _onListening() {
    console.log(`IMAP Server is listening on ${this.server.address().address}:${this.server.address().port}`);
  }

  _onClose() {
    console.log('IMAP Server closed all connections and shut down.');
  }

  _onError(err) {
    console.error('IMAP Server error:', err.message);
  }

  _handleProxy(socket, callback) {
    if (this.options.proxy && this.options.proxy.parse) {
      const parser = require('proxy-protocol').Parser;
      const p = new parser();

      p.on('end', (info) => {
        if (callback) callback(info);
      });

      p.on('error', (err) => {
        socket.destroy(err);
      });

      socket.on('data', (data) => {
        p.execute(data);
      });

      socket.once('end', () => {
        p.end();
      });
    } else {
      this._upgrade(socket, callback);
    }
  }

  _upgrade(socket, callback) {
    if (this.options.tls) {
      const tlsSocket = tls.connect({
        ...this.options.tls,
        socket: socket,
        rejectUnauthorized: false,
      });

      tlsSocket.on('secureConnect', () => {
        if (callback) callback(tlsSocket);
      });

      tlsSocket.on('error', (err) => {
        socket.destroy(err);
      });
    } else {
      if (callback) callback(socket);
    }
  }
}
