class TcpReader {
  constructor(transporter, opts) {
    this.transporter = transporter;
    this.opts = opts || {};
    this.sockets = [];
    this.logger = transporter.logger;
    this.server = null;
    this.connected = false;
  }

  async listen() {
    return new Promise((resolve, reject) => {
      const { port } = this.opts;

      this.server = require('net').createServer();

      this.server.on('listening', () => {
        this.connected = true;
        this.logger.info(`TCP server listening on port ${port}`);
        resolve();
      });

      this.server.on('error', (err) => {
        this.logger.error(`TCP server error: ${err.message}`);
        reject(err);
      });

      this.server.on('connection', (socket) => {
        this.onTcpClientConnected(socket);
      });

      this.server.listen(port);
    });
  }

  onTcpClientConnected(socket) {
    socket.setEncoding('utf8');
    this.sockets.push(socket);

    socket.on('data', (data) => {
      try {
        this.transporter.handleIncomingMessage(data);
      } catch (err) {
        this.logger.error(`Error processing TCP data: ${err.message}`);
        this.closeSocket(socket, err);
      }
    });

    socket.on('end', () => {
      this.closeSocket(socket);
    });

    socket.on('error', (err) => {
      this.logger.error(`Socket error: ${err.message}`);
      this.closeSocket(socket, err);
    });
  }

  closeSocket(socket, err) {
    if (!socket.destroyed) {
      socket.destroy(err);
    }
    const index = this.sockets.indexOf(socket);
    if (index !== -1) {
      this.sockets.splice(index, 1);
    }
  }

  close() {
    if (!this.connected) return;

    this.connected = false;
    this.sockets.forEach((socket) => {
      this.closeSocket(socket);
    });
    this.sockets = [];

    if (this.server && this.server.listening) {
      this.server.close(() => {
        this.logger.info('TCP server closed.');
      });
    }
  }
}
