class RedisInterceptor {
  constructor(Pythagora, listenPort, targetPort, intermediateData) {
    this.Pythagora = Pythagora;
    this.listenPort = listenPort;
    this.targetPort = targetPort || 6379;
    this.intermediateData = intermediateData || [];
    this.listenSocket = null;
    this.targetSocket = null;
    this.mode = undefined;
  }

  async init() {
    return new Promise((resolve, reject) => {
      this.listenSocket = require('net').createServer();
      this.listenSocket.on('error', (err) => {
        console.error('TCP server error:', err);
        reject(err);
      });
      this.listenSocket.on('listening', () => resolve());
      this.listenSocket.on('connection', (clientSocket) => {
        clientSocket.setEncoding('utf8');
        const clientAddress = clientSocket.remoteAddress + ':' + clientSocket.remotePort;

        if (this.mode === 'test') {
          let requestData = '';
          clientSocket.on('data', (data) => {
            requestData += data;
            const mockedResponse = this.findMockedResponse(requestData);
            if (mockedResponse) {
              clientSocket.write(mockedResponse);
            }
          });
        } else {
          const redisSocket = new require('net').Socket();
          redisSocket.connect(this.targetPort, '127.0.0.1');

          clientSocket.on('data', (data) => {
            if (this.mode === 'capture') {
              this.forwardData(clientSocket, data, true);
            } else {
              this.forwardData(clientSocket, data, false);
            }
          });

          redisSocket.on('data', (data) => {
            clientSocket.write(data);
          });

          clientSocket.on('end', () => {
            redisSocket.end();
          });

          redisSocket.on('end', () => {
            clientSocket.end();
          });

          redisSocket.on('error', (err) => {
            console.error(`Redis connection error for ${clientAddress}:`, err);
            clientSocket.destroy();
          });
        }
      });

      this.listenSocket.listen(this.listenPort);
    });
  }

  forwardData(connection, data, saveData) {
    const redisSocket = new require('net').Socket();
    redisSocket.connect(this.targetPort, '127.0.0.1');

    redisSocket.write(data);

    let responseData = '';

    redisSocket.on('data', (chunk) => {
      responseData += chunk;
    });

    redisSocket.on('end', () => {
      if (saveData) {
        this.Pythagora.saveCapturedData({ request: data, response: responseData });
      }
      connection.write(responseData);
    });

    redisSocket.on('error', (err) => {
      console.error('Error in Redis socket:', err);
    });
  }

  splitIntoChunks(str, chunkSize) {
    const chunks = [];
    for (let i = 0; i < str.length; i += chunkSize) {
      chunks.push(str.slice(i, i + chunkSize));
    }
    return chunks;
  }

  findMockedResponse(requestData) {
    const normalizedRequest = requestData.trim().toLowerCase();
    for (const pair of this.intermediateData) {
      if (pair.request.trim().toLowerCase() === normalizedRequest) {
        return pair.response;
      }
    }
    return null;
  }
}
