const fs = require('fs');
const path = require('path');
const axios = require('axios');

const API_VERSION_URL = 'https://api.example.com/versions';
const API_SCHEMA_URL = 'https://api.example.com/schemas/';
const CACHE_DIR = path.join(__dirname, 'cache');

async function fetchAndCacheVersions() {
  if (!fs.existsSync(CACHE_DIR)) {
    fs.mkdirSync(CACHE_DIR, { recursive: true });
  }

  try {
    const versionResponse = await axios.get(API_VERSION_URL);
    const versions = versionResponse.data;

    console.log('Fetched version data successfully.');

    for (const version of versions) {
      if (version.deprecated) continue;

      const schemaResponse = await axios.get(`${API_SCHEMA_URL}${version.id}`);
      const schemaData = schemaResponse.data;

      const cacheFilePath = path.join(CACHE_DIR, `schema_v${version.id}.json`);
      fs.writeFileSync(cacheFilePath, JSON.stringify(schemaData, null, 2));

      console.log(`Cached schema for version ${version.id} at ${cacheFilePath}`);
    }

    console.log('All non-deprecated schema data has been cached.');
  } catch (error) {
    console.error('Error fetching or caching data:', error.message);
    process.exit(1);
  }
}

fetchAndCacheVersions();
