        const dgram = require('dgram');
        this.discoveryListener = dgram.createSocket('udp4');

        this.discoveryListener.on('message', (msg, rinfo) => {
            this.discoveryReceive(msg, rinfo);
        });

        this.discoveryListener.on('error', (err) => {
            console.error(`UDP discovery listener error:\n${err.stack}`);
            this.discoveryListener.close();
        });

        this.discoveryListener.bind(this.listenPort, () => {
            if (callback) callback();
        });
    }

    discoveryBroadcast(type, message, callback) {
        const client = require('dgram').createSocket('udp4');
        const data = JSON.stringify({ type, ...message });

        client.send(data, this.broadcastPort, this.broadcastIP, (err) => {
            if (err) console.error(`UDP broadcast error: ${err}`);
            else if (callback) callback();
            client.close();
        });
    }

    discoveryReceive(msg, rinfo) {
        try {
            const packet = JSON.parse(msg.toString());
            if (packet.type === 'heartbeat') {
                const hostname = packet.hostname || rinfo.address;
                this.nearbyServers[hostname] = {
                    host: rinfo.address,
                    port: packet.port,
                    timestamp: Date.now(),
                    info: packet.info
                };
            }
        } catch (e) {
            console.warn('Received invalid discovery message:', e.message);
        }
    }

    shutdownDiscovery() {
        if (this.discoveryListener) {
            this.discoveryListener.close();
            this.discoveryListener = null;
        }
    }

    calcBroadcastIP() {
        const os = require('os');
        const ifaces = os.networkInterfaces();
        for (const dev in ifaces) {
            for (const alias of ifaces[dev]) {
                if (!alias.internal && alias.family === 'IPv4') {
                    return alias.broadcast;
                }
            }
        }
        return '255.255.255.255';
    }
}
