class MasterIPC {
  constructor(options) {
    this.options = options || {};
    this._sock = null;
    this.children = [];
    this.sock = this.IPCSocket();
  }

  IPCSocket() {
    if (this._sock) return this._sock;

    const dgram = require('dgram');
    this._sock = dgram.createSocket('udp4');

    this._sock.on('message', (msg, rinfo) => {
      try {
        const data = JSON.parse(msg.toString());
        const worker = this.children.find(c => c.id === data.id);
        if (worker && this.invoke(data.method, data.args, worker)) {
          this.sock.send(JSON.stringify({ id: worker.id, response: 'ack' }), this.options.port, this.options.address);
        }
      } catch (e) {
        console.error(e);
      }
    });

    return this._sock;
  }

  setupIPC() {
    this._sock.bind(this.options.port, this.options.address, () => {
      this._sock.setBroadcast(true);
      this._sock.addMembership(this.options.address);
      this.emit('start');
    });
  }

  call(id, method, ...args) {
    const message = { id, method, args };
    this.sock.send(JSON.stringify(message), this.options.port, this.options.address);
  }

  invoke(method, args, worker) {
    if (!worker || !worker[method]) return false;
    worker[method].apply(worker, args);
    return true;
  }

  connect(worker) {
    this.children.push(worker);
    this.emit('worker connected', worker);
  }

  connectMaster(sig) {
    if (this._sock._handle?.address().port) {
      process.kill(process.pid, sig);
    } else {
      this._sock.on('listening', () => {
        process.kill(process.pid, sig);
      });
    }
  }

  masterKilled(data) {
    this.emit('restart', data);
  }

  emit(event, ...args) {
    if (this[`on${event}`] instanceof Function) {
      this[`on${event}`](...args);
    }
  }
}
