const EventEmitter = require('events');

class Chrome extends EventEmitter {
  constructor(options = {}, notifier = new EventEmitter()) {
    super();
    this.host = options.host || 'localhost';
    this.port = options.port || 9222;
    this.secure = options.secure === true;
    this.webSocketUrl = `${this.secure ? 'wss' : 'ws'}://${this.host}:${this.port}/devtools/browser`;
    this._ws = null;
    this._notifier = notifier;
    this._callbacks = {};
    this._nextCommandId = 1;
    this._start();
  }

  async _start() {
    await this._connectToWebSocket();
  }

  async _connectToWebSocket() {
    const WebSocket = require('ws');
    this._ws = new WebSocket(this.webSocketUrl);

    this._ws.on('open', () => {
      this.emit('connected');
    });

    this._ws.on('message', (data) => {
      this._handleMessage(JSON.parse(data.toString()));
    });

    this._ws.on('error', (err) => {
      this.emit('error', err);
    });

    this._ws.on('close', () => {
      this._handleConnectionClose();
    });
  }

  _handleConnectionClose() {
    this.emit('disconnected');
    this._ws = null;
  }

  _handleMessage(message) {
    if ('id' in message && message.id in this._callbacks) {
      const callback = this._callbacks[message.id];
      delete this._callbacks[message.id];
      callback(null, message);
    } else if ('method' in message) {
      this._notifier.emit(message.method, message.params);
    }
  }

  send(method, params = {}, sessionId, callback) {
    return new Promise((resolve, reject) => {
      if (!this._ws || this._ws.readyState !== this._ws.OPEN) {
        reject(new Error('WebSocket is not open.'));
        return;
      }

      const id = this._nextCommandId++;
      const command = {
        id,
        method,
        params,
      };

      if (sessionId) {
        command.sessionId = sessionId;
      }

      this._callbacks[id] = (err, result) => {
        if (err) {
          reject(err);
        } else {
          resolve(result);
        }
        if (callback) {
          callback(err, result);
        }
      };

      this._enqueueCommand(command);
    });
  }

  _enqueueCommand(command) {
    this._ws.send(JSON.stringify(command));
  }

  close(callback) {
    return new Promise((resolve, reject) => {
      if (!this._ws) {
        resolve();
        return;
      }

      this._ws.removeAllListeners();
      this._ws.close(() => {
        this._handleConnectionClose();
        resolve();
        if (callback) {
          callback();
        }
      });
    });
  }
}
