    this.secure = options.secure !== undefined ? options.secure : true;
    this._notifier = notifier;
    this._callbacks = {};
    this._nextCommandId = 1;
    this.webSocketUrl = `${this.secure ? 'wss' : 'ws'}://${this.host}:${this.port}/devtools/browser`;
    this._ws = null;
  }

  async _connectToWebSocket() {
    this._ws = new WebSocket(this.webSocketUrl);

    this._ws.on('open', () => {
      this.emit('connected');
    });

    this._ws.on('message', (data) => {
      try {
        const message = JSON.parse(data);
        this._handleMessage(message);
      } catch (e) {
        this.emit('error', e);
      }
    });

    this._ws.on('close', (code, reason) => {
      this.emit('disconnected', code, reason);
    });

    this._ws.on('error', (err) => {
      this.emit('error', err);
    });
  }

  _handleMessage(message) {
    if (message.id && this._callbacks[message.id]) {
      const callback = this._callbacks[message.id];
      delete this._callbacks[message.id];
      if (message.error) {
        callback(new Error(message.error.message), null);
      } else {
        callback(null, message.result);
      }
    } else if (message.method) {
      this.emit(message.method, message.params);
    }
  }

  _enqueueCommand(method, params = {}, sessionId = '', callback) {
    const commandId = this._nextCommandId++;
    const command = {
      id: commandId,
      method,
      params,
    };
    if (sessionId) {
      command.sessionId = sessionId;
    }
    this._ws.send(JSON.stringify(command));
    if (callback) {
      this._callbacks[commandId] = callback;
    }
  }

  send(method, ...args) {
    let params = {};
    let sessionId = '';
    let callback;

    if (args.length === 1 && typeof args[0] === 'function') {
      callback = args[0];
    } else if (args.length >= 1) {
      params = args[0];
      if (args.length > 1 && typeof args[args.length - 1] === 'function') {
        callback = args[args.length - 1];
      }
      if (args.length > 1 && typeof args[1] === 'string') {
        sessionId = args[1];
      }
    }

    return new Promise((resolve, reject) => {
      this._enqueueCommand(
        method,
        params,
        sessionId,
        (err, result) => {
          if (err) {
            reject(err);
          } else {
            resolve(result);
          }
        }
      );
    });
  }
}
