    this._ws.on('close', (code, reason) => {
      this._onConnectionClosed(code, reason);
    });

    this._ws.on('error', (err) => {
      console.error('WebSocket error:', err);
      if (!this._requiredToFail) {
        this._onConnectionClosed(1006, 'abnormal closure');
      }
    });
