    const event = {
      data: isBinary ? data : data.toString(),
      origin: this.url,
      lastEventId: '',
      source: this,
      currentTarget: this,
      target: this,
    };
    if (typeof this._globalObject.dispatchEvent === 'function') {
      this._globalObject.dispatchEvent(new MessageEvent('message', event));
    } else {
      console.warn('dispatchEvent not available on global object');
    }
  }

  _onConnectionClosed(code, reason) {
    this._readyState = 3; // CLOSED
    this._ws = null;
    this._sendQueue = [];
    this._dequeueScheduled = false;

    const event = {
      wasClean: true,
      code: code,
      reason: reason || '',
      currentTarget: this,
      target: this,
    };

    if (typeof this._globalObject.dispatchEvent === 'function') {
      this._globalObject.dispatchEvent(new CloseEvent('close', event));
    } else {
      console.warn('dispatchEvent not available on global object');
    }
  }

