class WebSocketImpl {
  constructor(globalObject, args, privateData) {
    this._globalObject = globalObject;
    this._ownerDocument = globalObject.document;
    this._urlRecord = new URL(args[0]);
    this.url = args[0];
    this._ws = null;
    this._readyState = 0; // CONNECTING
    this._requiredToFail = false;
    this.bufferedAmount = 0;
    this._sendQueue = [];

    const WebSocket = require('ws');
    this._ws = new WebSocket(this.url);

    this._ws.on('open', () => this._onConnectionEstablished());
    this._ws.on('message', (data, isBinary) => this._onMessageReceived(data, isBinary));
    this._ws.on('close', (code, reason) => this._onConnectionClosed(code, reason));
    this._ws.on('error', (err) => {
      console.error('WebSocket error:', err);
      if (!this._requiredToFail) {
        this._requiredToFail = true;
        this._onConnectionClosed(1006, 'Abnormal closure due to error');
      }
    });
  }

  _onConnectionEstablished() {
    this._readyState = 1; // OPEN
    for (const message of this._sendQueue) {
      this._ws.send(message.data, { binary: message.isBinary }, (err) => {
        if (err) {
          console.error('Failed to send queued message:', err);
        } else {
          this.bufferedAmount -= message.size;
        }
      });
    }
    this._sendQueue = [];
  }

  _onMessageReceived(data, isBinary) {
    const event = new MessageEvent('message', {
      data: isBinary ? data : data.toString(),
      origin: this._urlRecord.origin,
      lastEventId: '',
      source: this
    });
    this.dispatchEvent(event);
  }

  _onConnectionClosed(code, reason) {
    this._readyState = 3; // CLOSED
    const event = new CloseEvent('close', {
      wasClean: code === 1000,
      code: code,
      reason: reason,
      bubbles: false,
      cancelable: false
    });
    this.dispatchEvent(event);
  }

  close(code, reason) {
    if (this._readyState === 1 || this._readyState === 0) {
      this._ws.close(code, reason);
    }
  }

  send(data) {
    if (this._readyState !== 1) {
      throw new Error('Invalid state to send data');
    }

    let isBinary = false;
    let size = 0;

    if (typeof data === 'string') {
      size = Buffer.byteLength(data);
    } else if (data instanceof Blob) {
      isBinary = true;
      size = data.size;
    } else {
      throw new TypeError('Unsupported data type');
    }

    if (this._ws.readyState === WebSocket.OPEN) {
      this._ws.send(data, { binary: isBinary }, (err) => {
        if (err) {
          console.error('Failed to send message:', err);
        } else {
          this.bufferedAmount += size;
        }
      });
    } else {
      this._sendQueue.push({ data, isBinary, size });
    }
  }

  get readyState() {
    return this._readyState;
  }

  dispatchEvent(event) {
    const handler = this[`on${event.type}`];
    if (handler && typeof handler === 'function') {
      handler.call(this, event);
    }
  }
}

class MessageEvent {
  constructor(type, eventInitDict) {
    this.type = type;
    this.data = eventInitDict.data;
    this.origin = eventInitDict.origin;
    this.lastEventId = eventInitDict.lastEventId;
    this.source = eventInitDict.source;
  }
}

class CloseEvent {
  constructor(type, eventInitDict) {
    this.type = type;
    this.wasClean = eventInitDict.wasClean;
    this.code = eventInitDict.code;
    this.reason = eventInitDict.reason;
    this.bubbles = eventInitDict.bubbles;
    this.cancelable = eventInitDict.cancelable;
  }
}
