        this.handleIncomingMessage(event.data);
      };

      this.websocket.onerror = () => {
        console.error('WebSocket error occurred.');
        this.handleError();
      };

      this.websocket.onclose = (event) => {
        console.log(`WebSocket connection closed: ${event.reason}`);
        this.handleClose(event.code);
      };
    } catch (error) {
      console.error('Failed to establish WebSocket connection:', error);
      this.handleError();
    }
  }

  sendMessage(data) {
    if (this.websocket && this.websocket.readyState === WebSocket.OPEN) {
      const compressedData = this.compressData(data);
      this.websocket.send(compressedData);
    } else {
      console.warn('WebSocket is not open. Message could not be sent.');
    }
  }

  closeConnection() {
    if (this.websocket) {
      this.websocket.close();
    }
  }

  handleIncomingMessage(message) {
    console.log('Received message:', message);
    // Process the incoming message as needed
  }

  handleError() {
    this.wxErrorCount++;
    console.log(`Retrying connection... Attempt #${this.wxErrorCount}`);
    this.scheduleReconnect();
  }

  handleClose(code) {
    if (code !== 1000) {
      this.scheduleReconnect();
    }
  }

  scheduleReconnect() {
    clearTimeout(this.retryTimer);
    const delay = Math.min(5000 * Math.pow(2, this.wxErrorCount), 60000);
    this.retryTimer = setTimeout(() => {
      this.openWebSocket();
    }, delay);
  }

  startHeartbeat() {
    setInterval(() => {
      if (this.websocket && this.websocket.readyState === WebSocket.OPEN) {
        this.sendMessage({ type: 'heartbeat' });
      }
    }, 30000);
  }

  compressData(data) {
    return JSON.stringify(data);
  }
}
