class RPCWebSocket {
    constructor(address, encryption, namespace) {
        this._namespace = namespace || '';
        this._listeners = {};
        this._handles = {};
        this.setAddress(address, encryption);
    }

    setAddress(address, encryption) {
        const protocol = encryption ? 'wss' : 'ws';
        this._socket = new WebSocket(`${protocol}://${address}`);
        this._socket.onopen = () => this._onopen();
        this._socket.onmessage = (event) => this._onmessage(event.data);
        this._socket.onerror = (error) => this._onerror(error);
        this._socket.onclose = () => this._onclose();
    }

    addListener(method, callback) {
        if (!this._listeners[method]) {
            this._listeners[method] = [];
        }
        this._listeners[method].push(callback);
    }

    removeListener(method) {
        delete this._listeners[method];
    }

    request(method, params, id, successCallback, errorCallback) {
        const fullMethod = this._namespace + method;
        const data = this._formatData(fullMethod, params, id);
        this._handles[id] = { success: successCallback, error: errorCallback };
        this._send(data);
    }

    batchRequest(requests, successCallback, errorCallback) {
        const formattedRequests = requests.map(req => {
            const fullMethod = this._namespace + req.method;
            return this._formatData(fullMethod, req.params, req.id);
        });
        const batchData = JSON.stringify(formattedRequests);
        this._send(batchData);
    }

    _formatData(method, params, id) {
        return JSON.stringify({
            jsonrpc: '2.0',
            method,
            params,
            id
        });
    }

    _send(data) {
        if (this._socket.readyState === WebSocket.OPEN) {
            this._socket.send(data);
        } else {
            setTimeout(() => this._send(data), 1000);
        }
    }

    _onopen() {
        // Handle open event if needed
    }

    _onclose() {
        // Handle close event and reconnect logic if needed
    }

    _onerror(error) {
        for (const id in this._handles) {
            const handle = this._handles[id];
            if (handle && handle.error) {
                handle.error(error);
            }
        }
    }

    _onmessage(message) {
        try {
            const response = JSON.parse(message);
            if (response.id !== undefined) {
                const handle = this._handles[response.id];
                if (handle) {
                    if (response.error) {
                        if (handle.error) handle.error(response.error);
                    } else {
                        if (handle.success) handle.success(response.result);
                    }
                    delete this._handles[response.id];
                }
            } else if (response.method) {
                const method = response.method;
                const listeners = this._listeners[method];
                if (listeners) {
                    for (const callback of listeners) {
                        callback(response.params);
                    }
                }
            }
        } catch (e) {
            console.error('Error parsing message:', e);
        }
    }
}
