class Client {
  constructor(req, socket, head, options) {
    this.options = options || {};
    this.id = this.uniqueId('client_');
    this.ws = new WebSocket(`ws://${req.headers.host}`);
    this.plugins = [];
    this.url = '';
    this._setupEventHandlers();
  }

  _setupEventHandlers() {
    this.ws.onmessage = (event) => this.message(event);
    this.ws.onclose = (event) => this.close(event);
  }

  message(event) {
    const data = this._data(event);
    if (!data || !data.command) return;

    switch (data.command) {
      case 'info':
        this.info(data.payload);
        break;
      default:
        console.warn('Unknown command:', data.command);
    }
  }

  close(event) {
    this.ws.close();
    this.emit('end', event);
  }

  hello() {
    this.send({
      command: 'hello',
      payload: {
        protocols: ['v1'],
        name: 'WebSocketClient'
      }
    });
  }

  info(data) {
    this.plugins = data.plugins || [];
    this.url = data.url || '';
  }

  reload(files) {
    files.forEach(file => {
      this.send({
        command: 'reload',
        payload: {
          file,
          live: true
        }
      });
    });
  }

  _data(event) {
    try {
      return JSON.parse(event.data);
    } catch (e) {
      console.error('Failed to parse message:', e);
      return null;
    }
  }

  send(data) {
    this.ws.send(JSON.stringify(data));
  }

  uniqueId(prefix = '') {
    return `${prefix}${Math.random().toString(36).substr(2, 9)}`;
  }

  emit(event, data) {
    if (this[`on${event}`] && typeof this[`on${event}`] === 'function') {
      this[`on${event}`](data);
    }
  }
}
