class WebSocketClient {
  constructor(wsURL, disconnectTimeout = 5000, reconnectTimeout = 3000) {
    this.DISCONNECT_TIMEOUT = disconnectTimeout;
    this.RECONNECT_TIMEOUT = reconnectTimeout;
    this.wsState = WebSocket.CLOSED;
    this.ws = null;
    this.wsURL = wsURL;
    this.connectTS = 0;
    this.disconnectTID = null;
    this.reconnectTID = null;
    this.onmessage = null;
  }

  onconnect() {
    if (this.ws && this.ws.readyState === WebSocket.CONNECTING) return true;

    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      return true;
    }

    this.ondisconnect();
    this.ws = new WebSocket(this.wsURL);
    this.ws.onopen = this.onopen.bind(this);
    this.ws.onclose = this.onclose.bind(this);
    this.ws.onmessage = (e) => {
      if (this.onmessage) {
        try {
          const data = JSON.parse(e.data);
          this.onmessage(data);
        } catch (err) {
          console.error('Failed to parse message:', err);
        }
      }
    };
    this.ws.onerror = (e) => {
      console.error('WebSocket error:', e);
    };

    this.connectTS = Date.now();
    this.wsState = WebSocket.CONNECTING;
    return true;
  }

  ondisconnect() {
    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }

    if (this.reconnectTID) {
      clearTimeout(this.reconnectTID);
      this.reconnectTID = null;
    }

    if (this.disconnectTID) {
      clearTimeout(this.disconnectTID);
      this.disconnectTID = null;
    }

    this.wsState = WebSocket.CLOSED;
  }

  onopen(e) {
    this.wsState = WebSocket.OPEN;
    this.disconnectTID = setTimeout(() => {
      this.ondisconnect();
    }, this.DISCONNECT_TIMEOUT);
  }

  onclose() {
    this.wsState = WebSocket.CLOSED;

    if (Date.now() - this.connectTS < 1000) {
      this.reconnectTID = setTimeout(() => this.reconnect(), this.RECONNECT_TIMEOUT);
      return true;
    }

    return false;
  }

  send(e) {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify(e));
    } else {
      console.warn('WebSocket is not open. Message not sent:', e);
    }
  }

  reconnect() {
    this.ondisconnect();
    this.onconnect();
  }
}
