function initWebSocket(handleFunctionCall, url, cb) {
  let ws;
  let reconnectAttempts = 0;
  const maxReconnectAttempts = 5;
  const reconnectInterval = 5000;

  function connect() {
    try {
      ws = new WebSocket(url);

      ws.onopen = () => {
        postMessage({ type: 'connect', status: 'open' });
        cb(ws);
        reconnectAttempts = 0;
      };

      ws.onmessage = (event) => {
        handleFunctionCall(event.data);
      };

      ws.onclose = (event) => {
        postMessage({ type: 'connect', status: 'close', code: event.code, reason: event.reason });
        if (reconnectAttempts < maxReconnectAttempts) {
          setTimeout(() => {
            reconnectAttempts++;
            connect();
          }, reconnectInterval * Math.pow(2, reconnectAttempts - 1));
        }
      };

      ws.onerror = (error) => {
        postMessage({ type: 'connect', status: 'error', error: error.message });
      };
    } catch (e) {
      console.error('WebSocket connection failed:', e);
    }
  }

  connect();
}
