class ReconnectingWebSocket {
  constructor(url, protocols = null, options = {}) {
    this.url = url;
    this.protocols = protocols;
    this.debug = options.debug || false;
    this.automaticOpen = options.automaticOpen !== false;
    this.reconnectInterval = options.reconnectInterval || 1000;
    this.maxReconnectInterval = options.maxReconnectInterval || 30000;
    this.reconnectDecay = options.reconnectDecay || 1.5;
    this.timeoutInterval = options.timeoutInterval || 2000;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CONNECTING;
    this.protocol = null;

    this.addEventListener('open', () => {
      this.onopen();
    });

    this.addEventListener('close', (event) => {
      this.onclose(event);
    });

    this.addEventListener('connecting', () => {
      this.onconnecting();
    });

    this.addEventListener('message', (event) => {
      this.onmessage(event);
    });

    this.addEventListener('error', (event) => {
      this.onerror(event);
    });

    if (this.automaticOpen) {
      this.open(false);
    }
  }

  addEventListener(type, listener) {
    if (!this._listeners) this._listeners = {};
    if (!this._listeners[type]) this._listeners[type] = [];
    this._listeners[type].push(listener);
  }

  removeEventListener(type, listener) {
    if (!this._listeners || !this._listeners[type]) return;
    const index = this._listeners[type].indexOf(listener);
    if (index >= 0) {
      this._listeners[type].splice(index, 1);
    }
  }

  dispatchEvent(event) {
    if (!this._listeners || !this._listeners[event.type]) return;
    for (const listener of this._listeners[event.type]) {
      listener(event);
    }
  }

  open(reconnecting) {
    if (this.debug) console.log(`Connecting to ${this.url}`);
    this.dispatchEvent({ type: 'connecting' });
    this.readyState = WebSocket.CONNECTING;

    const ws = new WebSocket(this.url, this.protocols);
    this.ws = ws;

    const timeout = setTimeout(() => {
      if (this.debug) console.log('Connection timeout');
      this.dispatchEvent({ type: 'error', error: 'timeout' });
      this.close();
    }, this.timeoutInterval);

    ws.onopen = () => {
      clearTimeout(timeout);
      this.reconnectAttempts = 0;
      this.protocol = ws.protocol;
      this.readyState = WebSocket.OPEN;
      this.dispatchEvent({ type: 'open' });
    };

    ws.onclose = (event) => {
      if (this.debug) console.log(`Connection closed: ${event.reason}`);
      this.readyState = WebSocket.CLOSED;
      this.dispatchEvent({ type: 'close', wasClean: event.wasClean, code: event.code, reason: event.reason });

      if (reconnecting === true) {
        const reconnectTimeout = Math.min(
          this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts),
          this.maxReconnectInterval
        );
        this.reconnectAttempts++;
        setTimeout(() => {
          this.open(true);
        }, reconnectTimeout);
      }
    };

    ws.onmessage = (event) => {
      this.dispatchEvent({ type: 'message', data: event.data });
    };

    ws.onerror = (event) => {
      this.dispatchEvent({ type: 'error', error: event.error });
    };
  }

  send(data) {
    if (this.readyState === WebSocket.OPEN) {
      this.ws.send(data);
    } else {
      throw new Error('WebSocket is not open: readyState ' + this.readyState);
    }
  }

  close(code, reason) {
    if (this.ws) {
      this.ws.close(code, reason);
    }
  }

  refresh() {
    if (this.readyState === WebSocket.OPEN || this.readyState === WebSocket.CONNECTING) {
      this.close();
    }
    this.open(true);
  }

  onopen() {}

  onclose() {}

  onconnecting() {}

  onmessage() {}

  onerror() {}
}
