class WebSocketStore {
  constructor(snack, currentUser) {
    this.snack = snack;
    this.currentUser = currentUser;
    this.wsActive = false;
    this.ws = null;
    this.reconnectAttempts = 0;
    this.maxReconnectAttempts = 5;
    this.reconnectDelay = 3000;
  }

  listen(callback) {
    if (this.wsActive) return;

    const token = this.currentUser.getToken();
    const wsUrl = `wss://example.com/socket?token=${encodeURIComponent(token)}`;
    this.ws = new WebSocket(wsUrl);

    this.ws.onopen = () => {
      this.wsActive = true;
      this.reconnectAttempts = 0;
      console.log('WebSocket connection opened');
    };

    this.ws.onmessage = (event) => {
      try {
        const msg = JSON.parse(event.data);
        callback(msg);
      } catch (e) {
        console.error('Error parsing message:', e);
      }
    };

    this.ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      this.handleReconnect();
    };

    this.ws.onclose = (event) => {
      this.wsActive = false;
      this.handleReconnect();
    };
  }

  close() {
    if (!this.ws || !this.wsActive) return;
    this.ws.close(1000);
  }

  handleReconnect() {
    if (this.reconnectAttempts >= this.maxReconnectAttempts) {
      this.snack.show('Failed to reconnect. Please check your internet connection.');
      return;
    }

    setTimeout(() => {
      this.reconnectAttempts++;
      this.listen((msg) => {
        // Assuming the same callback is used for reconnection
        // You might want to pass it in or store it as an instance variable
      });
    }, this.reconnectDelay * Math.pow(2, this.reconnectAttempts));
  }
}
