class WebSocketRPC {
  constructor() {
    this._ = window._;
    this.JSON = JSON;
    this.utils = {
      uuid: () => {
        return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
          const r = Math.random() * 16 | 0,
            v = c == 'x' ? r : (r & 0x3 | 0x8);
          return v.toString(16);
        });
      }
    };
    this.alerts = {
      show: (msg) => console.log(msg)
    };
    this.initialized = false;
    this.handles = [];
    this.sock = null;
    this.conf = null;
    this.scheme = 'ws';
    this.onready = null;
  }

  onerror(ev) {
    this.handles.forEach(h => {
      h.error && h.error('WebSocket error', ev);
    });
    this.handles = [];
    this.initialized = false;
    this.alerts.show('WebSocket error occurred');
  }

  onclose(ev) {
    this.handles.forEach(h => {
      h.error && h.error('Connection closed', ev);
    });
    this.handles = [];
    this.initialized = false;
    this.alerts.show('WebSocket connection closed');
  }

  onopen() {
    this.initialized = true;
    if (this.onready) this.onready();
  }

  onmessage(message) {
    try {
      const data = this.JSON.parse(message.data);
      if (!data.id) return;

      const handle = this._.find(this.handles, { id: data.id });
      if (!handle) return;

      if (data.error) {
        handle.error && handle.error(data.error.message || 'Server error', data.error);
      } else {
        handle.success && handle.success(data.result);
      }

      this.handles = this._.without(this.handles, handle);
    } catch (e) {
      this.onerror(e);
    }
  }

  invoke(opts) {
    if (!this.initialized) {
      opts.error && opts.error('Not connected');
      return;
    }

    const id = this.utils.uuid();
    const req = {
      jsonrpc: '2.0',
      method: opts.name,
      params: opts.params || [],
      id: id
    };

    this.handles.push({
      id: id,
      success: opts.success,
      error: opts.error
    });

    this.sock.send(this.JSON.stringify(req));
  }

  init(conf, onready) {
    this.conf = conf;
    this.onready = onready;
    this.scheme = conf.encrypt ? 'wss' : 'ws';
    const auth = conf.auth ? `${conf.user}:${conf.pass}@` : '';
    const url = `${this.scheme}://${auth}${conf.host}:${conf.port}${conf.path || ''}`;
    this.sock = new WebSocket(url);

    this.sock.addEventListener('open', () => this.onopen());
    this.sock.addEventListener('message', (ev) => this.onmessage(ev));
    this.sock.addEventListener('error', (ev) => this.onerror(ev));
    this.sock.addEventListener('close', (ev) => this.onclose(ev));
  }
}
