function setupWebSocketConnection(url) {
    const ws = new WebSocket(url);

    const connection = {
        send(data) {
            if (ws.readyState === WebSocket.OPEN) {
                ws.send(data);
            } else {
                throw new Error('WebSocket is not open. Ready state: ' + ws.readyState);
            }
        },
        close() {
            ws.close();
        },
        setBinaryType(type) {
            if (type !== 'blob' && type !== 'arraybuffer') {
                throw new Error('Invalid binary type. Must be "blob" or "arraybuffer".');
            }
            ws.binaryType = type;
        }
    };

    return {
        ...connection,
        onopen: null,
        onmessage: null,
        onclose: null,
        onerror: null,
        connect: () => {
            ws.onopen = (event) => {
                if (this.onopen) this.onopen(event);
