function startRecording(data) {
  const { host, port, language, task, modelSize, useVad } = data;
  let ws;
  let mediaRecorder;
  let audioChunks = [];
  let isServerReady = false;
  let isCapturing = false;

  function connectWebSocket() {
    const url = `ws://${host}:${port}`;
    ws = new WebSocket(url);

    ws.onopen = () => {
      console.log("WebSocket connected");
      sendInitializationMessage();
    };

    ws.onmessage = (event) => {
      const message = JSON.parse(event.data);
      if (message.status === "ready") {
        isServerReady = true;
        startAudioCapture();
      } else if (message.type === "transcription") {
        browser.runtime.sendMessage({ type: "transcription", payload: message.text });
      } else if (message.type === "status") {
        browser.runtime.sendMessage({ type: "status", payload: message.message });
      }
    };

    ws.onerror = (error) => {
      console.error("WebSocket error:", error);
      browser.runtime.sendMessage({ type: "error", payload: "WebSocket connection error" });
    };

    ws.onclose = () => {
      console.log("WebSocket disconnected");
      isServerReady = false;
      isCapturing = false;
      browser.runtime.sendMessage({ type: "disconnected" });
    };
  }

  function sendInitializationMessage() {
    const initMessage = {
      command: "init",
      params: {
        language,
        task,
        modelSize,
        useVad,
      },
    };
    ws.send(JSON.stringify(initMessage));
  }

  async function startAudioCapture() {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const audioContext = new AudioContext();
      const source = audioContext.createMediaStreamSource(stream);
      const destination = audioContext.createMediaStreamDestination();
      source.connect(destination);

      mediaRecorder = new MediaRecorder(destination.stream);
      mediaRecorder.ondataavailable = (event) => {
        audioChunks.push(event.data);
      };

      mediaRecorder.onstart = () => {
        isCapturing = true;
        audioChunks = [];
      };

      mediaRecorder.onstop = () => {
        isCapturing = false;
        const blob = new Blob(audioChunks, { type: "audio/webm" });
        const reader = new FileReader();
        reader.onload = () => {
          const base64data = reader.result.split(",")[1];
          ws.send(base64data);
        };
        reader.readAsDataURL(blob);
      };

      mediaRecorder.start(1000); // Send every 1 second
    } catch (err) {
      console.error("Microphone access error:", err);
      browser.runtime.sendMessage({ type: "error", payload: "Failed to access microphone" });
    }
  }

  function stopRecording() {
    if (mediaRecorder && isCapturing) {
      mediaRecorder.stop();
    }
    if (ws) {
      ws.close();
    }
  }

  connectWebSocket();

  // Expose stopRecording for external control
  window.stopRecording = stopRecording;
}
