class BingChatWebSocket {
    constructor() {
        this.webSockets = {};
    }

    async getSocketId() {
        return new Promise((resolve, reject) => {
            const socketId = Math.random().toString(36).substring(2);
            const ws = new WebSocket('wss://example-chat-service.com');

            ws.onopen = () => {
                const handshakeMessage = JSON.stringify({ type: 'handshake', id: socketId });
                ws.send(handshakeMessage);
                this.webSockets[socketId] = ws;
                resolve(socketId);
            };

            ws.onerror = (error) => {
                reject(new Error('Failed to establish WebSocket connection'));
            };
        });
    }

    async ping(socketId) {
        return new Promise((resolve, reject) => {
            const ws = this.webSockets[socketId];
            if (!ws || ws.readyState !== WebSocket.OPEN) {
                reject(new Error('WebSocket is not open for the given socket ID'));
                return;
            }
            try {
                ws.send(JSON.stringify({ type: 'ping' }));
                resolve();
            } catch (error) {
                reject(error);
            }
        });
    }

    async send(socketId, msg, onMessage) {
        return new Promise((resolve, reject) => {
            const ws = this.webSockets[socketId];
            if (!ws || ws.readyState !== WebSocket.OPEN) {
                reject(new Error('WebSocket is not open for the given socket ID'));
                return;
            }

            let messageHandler = null;

            const handleIncomingMessage = (event) => {
                try {
                    const data = JSON.parse(event.data);
                    onMessage(data);
                    if (data.type === 2) {
                        ws.removeEventListener('message', messageHandler);
                        resolve(data);
                    }
                } catch (error) {
                    reject(error);
                }
            };

            messageHandler = handleIncomingMessage;
            ws.addEventListener('message', messageHandler);

            try {
                ws.send(JSON.stringify(msg));
            } catch (error) {
                reject(error);
            }
        });
    }

    async close(socketId) {
        return new Promise((resolve) => {
            const ws = this.webSockets[socketId];
            if (!ws) {
                resolve();
                return;
            }

            ws.close();
            delete this.webSockets[socketId];
            resolve();
        });
    }
}
