class CocogoatWebControl {
    constructor(_port = 32333) {
        this.port = _port;
        this.token = "";
        this.hwnd = 0;
        this.version = "1.0";
        this.ev = new (require("mitt"))();
        this.client = require("flyio").default;
        this.ws = undefined;
    }

    async authorize() {
        try {
            const res = await this.client.get(`http://localhost:${this.port}/auth`);
            if (res.status === 200 && res.data.token) {
                this.token = res.data.token;
                this.connectWebSocket();
                return true;
            }
        } catch (err) {
            console.error("Authorization failed:", err);
        }
        return false;
    }

    connectWebSocket() {
        const wsUrl = `ws://localhost:${this.port}/ws?token=${this.token}`;
        this.ws = new WebSocket(wsUrl);

        this.ws.onopen = () => {
            console.log("WebSocket connection established.");
        };

        this.ws.onmessage = (event) => {
            this.handleMessage(event.data);
        };

        this.ws.onerror = (error) => {
            console.error("WebSocket error:", error);
        };

        this.ws.onclose = () => {
            console.log("WebSocket connection closed.");
        };
    }

    handleMessage(data) {
        try {
            const message = JSON.parse(data);
            this.ev.emit(message.type, message.payload);
        } catch (e) {
            console.error("Failed to parse WebSocket message:", e);
        }
    }

    async wsInvoke(method, path, querystring = {}) {
        return new Promise((resolve, reject) => {
            if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
                reject(new Error("WebSocket is not connected."));
                return;
            }

            const id = Date.now().toString();
            const request = {
                id,
                method,
                path,
                query: querystring,
            };

            this.ws.send(JSON.stringify(request));

            const timeoutId = setTimeout(() => {
                reject(new Error("API invocation timeout."));
            }, 5000);

            this.ev.on(id, (response) => {
                clearTimeout(timeoutId);
                resolve({ status: response.status, body: response.body });
            });
        });
    }
}
