class WebSocketClient {
  constructor() {
    this.wsClient = null;
    this.sendQueue = [];
    this.pendingReconnect = null;
    this.onOpenCallback = null;
    this.onCloseCallback = null;
    this.onMessageCallback = null;
  }

  fireSendQueue() {
    if (this.getReadyState() !== WebSocket.OPEN) return;

    while (this.sendQueue.length > 0) {
      const { message, resolve, reject } = this.sendQueue.shift();
      try {
        this.wsClient.send(message);
        resolve();
      } catch (error) {
        reject(error);
      }
    }
  }

  clearSendQueue() {
    for (const { reject } of this.sendQueue) {
      reject(new Error('WebSocket connection closed before message could be sent'));
    }
    this.sendQueue = [];
  }

  planToReconnect(rpcUrl, options) {
    if (this.pendingReconnect) clearTimeout(this.pendingReconnect);

    this.pendingReconnect = setTimeout(() => {
      this.reconnect(rpcUrl, options);
    }, 5000);
  }

  init() {
    if (this.wsClient) {
      this.wsClient.close();
      this.wsClient = null;
    }
    this.clearSendQueue();
  }

  connect(rpcUrl, options, onOpenCallback, onCloseCallback, onMessageCallback) {
    this.init();

    this.onOpenCallback = onOpenCallback;
    this.onCloseCallback = onCloseCallback;
    this.onMessageCallback = onMessageCallback;

    this.wsClient = new WebSocket(rpcUrl, options?.protocols || undefined);

    this.wsClient.onopen = () => {
      this.fireSendQueue();
      if (this.onOpenCallback) this.onOpenCallback();
    };

    this.wsClient.onclose = (event) => {
      if (this.onCloseCallback) this.onCloseCallback(event);
      this.planToReconnect(rpcUrl, options);
    };

    this.wsClient.onmessage = (event) => {
      if (this.onMessageCallback) this.onMessageCallback(event);
    };

    this.wsClient.onerror = (error) => {
      console.error('WebSocket error:', error);
    };
  }

  reconnect(rpcUrl, options) {
    this.connect(rpcUrl, options, this.onOpenCallback, this.onCloseCallback, this.onMessageCallback);
  }

  send(requestContext) {
    return new Promise((resolve, reject) => {
      const message = JSON.stringify(requestContext);
      if (this.getReadyState() === WebSocket.OPEN) {
        try {
          this.wsClient.send(message);
          resolve();
        } catch (error) {
          reject(error);
        }
      } else {
        this.sendQueue.push({ message, resolve, reject });
      }
    });
  }

  getReadyState() {
    return this.wsClient ? this.wsClient.readyState : null;
  }
}
