class SnapdropServer {
  constructor(port) {
    const WebSocket = require('ws');
    this._wss = new WebSocket.Server({ port });
    this._rooms = {};

    this._wss.on('connection', (socket, request) => {
      const peer = { socket, ip: request.socket.remoteAddress };
      this._onConnection(peer);
    });

    this._wss.on('headers', (headers, request) => {
      this._onHeaders(headers, request);
    });
  }

  _onConnection(peer) {
    peer.socket.on('message', (data) => {
      this._onMessage(peer, data.toString());
    });

    peer.socket.on('close', () => {
      this._leaveRoom(peer);
    });

    this._joinRoom(peer);
    this._keepAlive(peer);
  }

  _onHeaders(headers, response) {
    headers.push('Access-Control-Allow-Origin: *');
  }

  _onMessage(sender, message) {
    try {
      const msg = JSON.parse(message);
      if (msg.type === 'peer') {
        const recipientIp = msg.data.ip;
        const room = this._rooms[recipientIp];
        if (room) {
          for (const peer of room) {
            if (peer !== sender) {
              this._send(peer, msg);
            }
          }
        }
      }
    } catch (e) {
      console.error('Invalid message format:', e);
    }
  }

  _send(peer, message) {
    if (peer.socket.readyState === WebSocket.OPEN) {
      peer.socket.send(JSON.stringify(message));
    }
  }

  _joinRoom(peer) {
    const ip = peer.ip;
    if (!this._rooms[ip]) {
      this._rooms[ip] = [];
    }
    this._rooms[ip].push(peer);
  }

  _leaveRoom(peer) {
    const ip = peer.ip;
    if (this._rooms[ip]) {
      this._rooms[ip] = this._rooms[ip].filter(p => p !== peer);
    }
  }

  _keepAlive(peer) {
    setInterval(() => {
      if (peer.socket.readyState === WebSocket.OPEN) {
        peer.socket.ping();
      }
    }, 30000);
  }
}
