async function getSubscribedTVChannels(lang = 'id') {
  try {
    // Step 1: Scrape initial configuration data from the webpage
    const configResponse = await axios.get('https://streamingservice.com/config');
    const configDataMatch = configResponse.data.match(/var config = ({.*?});/s);
    if (!configDataMatch) throw new Error('Failed to extract configuration data');
    const configData = JSON.parse(configDataMatch[1]);

    // Step 2: Authenticate with the service using extracted credentials
    const authResponse = await axios.post(
      'https://streamingservice.com/auth',
      {
        username: configData.username,
        password: configData.password,
      }
    );
    if (authResponse.status !== 200 || !authResponse.data.token) {
      throw new Error('Authentication failed');
    }

    const authToken = authResponse.data.token;

    // Step 3: Fetch subscribed channel packages
    const channelsResponse = await axios.get(
      'https://streamingservice.com/api/channels/subscribed',
      {
        headers: { Authorization: `Bearer ${authToken}` },
      }
    );

    if (channelsResponse.status !== 200) {
      throw new Error('Failed to fetch subscribed channels');
    }

    let allChannels = [];

    // Step 4: Process and deduplicate channel information
    for (const package of channelsResponse.data.packages) {
      for (const channel of package.channels) {
        allChannels.push({
          lang,
          site_id: channel.id,
          name: channel.name,
        });
      }
    }

    // Deduplicate by site_id
    const uniqueChannels = {};
    for (const channel of allChannels) {
      uniqueChannels[channel.site_id] = channel;
    }

    return Object.values(uniqueChannels);
  } catch (error) {
    throw new Error(`Error fetching TV channels: ${error.message}`);
  }
}
