class XVIZServer {
  constructor(handlers, options = {}, callback) {
    if (!handlers || handlers.length === 0) {
      throw new Error('XVIZServer requires at least one handler');
    }

    this.handlers = handlers;
    this.options = { logger: console, ...options };
    const WebSocket = require('ws');

    this._server = new WebSocket.Server(this.options);

    this._server.on('connection', (socket, request) => {
      this.handleSession(socket, request).catch((err) => {
        this.log('Error handling session:', err);
      });
    });

    if (callback) {
      this._server.on('listening', () => callback());
    }
  }

  get server() {
    return this._server;
  }

  close(cb) {
    this._server.close(cb);
  }

  async handleSession(socket, request) {
    for (const handler of this.handlers) {
      try {
        const accepted = await handler.accept(socket, request);
        if (accepted) {
          this.log('Session accepted by handler:', handler.constructor.name);
          return;
        }
      } catch (err) {
        this.log('Handler failed to accept session:', err);
      }
    }

    this.log('No handler accepted the session. Closing connection.');
    socket.close();
  }

  log(...msg) {
    if (this.options.logger && typeof this.options.logger.log === 'function') {
      this.options.logger.log(...msg);
    }
  }
}
