const axios = require('axios');
const fs = require('fs');
const path = require('path');

function downloadIcons(url, style, callback) {
  const targetImagePath = 'icons/';
  const duplicatesPath = 'duplicates.json';
  let duplicateMap = {};

  if (!fs.existsSync(targetImagePath)) {
    fs.mkdirSync(targetImagePath);
  }

  if (fs.existsSync(duplicatesPath)) {
    try {
      const data = fs.readFileSync(duplicatesPath, 'utf8');
      duplicateMap = JSON.parse(data);
    } catch (err) {
      console.error('Error reading duplicates file:', err);
    }
  }

  axios.get(url, {
    headers: {
      'Accept': 'application/json',
      'credentials': 'same-origin'
    }
  })
    .then(response => {
      const iconsData = response.data.icons;
      const processedIcons = [];
      const errors = [];

      iconsData.forEach(icon => {
        try {
          const name = icon.name;
          if (duplicateMap[name]) {
            throw new Error(`Duplicate icon name: ${name}`);
          }

          const svgUrl = icon.svgUrl.replace('{style}', style);
          return axios.get(svgUrl, { responseType: 'text' });
        } catch (err) {
          errors.push({ name: icon.name, error: err.message });
          return null;
        }
      });

      const svgPromises = iconsData.map((icon, index) =>
        new Promise((resolve) => {
          if (errors[index]) {
            resolve(null);
            return;
          }

          const name = icon.name;
          const svgUrl = icon.svgUrl.replace('{style}', style);

          axios.get(svgUrl, { responseType: 'text' })
            .then(svgResponse => {
              const iconName = `${name}.svg`;
              const filePath = path.join(targetImagePath, iconName);
              fs.writeFileSync(filePath, svgResponse.data);

              const processedIcon = {
                name,
                svg: iconName,
                category: icon.category || '',
                style: style,
                tags: icon.tags || [],
                code: icon.code || '',
                unicode: icon.unicode || '',
                pro: icon.pro || false
              };

              processedIcons.push(processedIcon);
              resolve(processedIcon);
            })
            .catch(err => {
              errors.push({ name: icon.name, error: err.message });
              resolve(null);
            });
        })
      );

      Promise.all(svgPromises).then(() => {
        if (errors.length > 0) {
          callback(errors, []);
        } else {
          processedIcons.sort((a, b) => a.name.localeCompare(b.name));
          callback(null, processedIcons);
        }

        if (errors.length > 0) {
          const errorNames = errors.map(e => e.name);
          errorNames.forEach(name => {
            if (!duplicateMap[name]) {
              duplicateMap[name] = true;
            }
          });

          fs.writeFileSync(duplicatesPath, JSON.stringify(duplicateMap, null, 2));
        }
      });
    })
    .catch(error => {
      callback([{ error: `Failed to fetch icon data from URL: ${url}`, message: error.message }], []);
    });
}
