    this._config = config || {};
    this._needStash = false;
    this._xhr = null;
    this._requestAbort = false;
    this._contentLength = null;
    this._receivedLength = 0;
    this._dataSource = null;
    this._range = null;
    this._requestURL = '';
  }

  open(dataSource, range) {
    this._dataSource = dataSource;
    this._range = range;
    const url = this._seekHandler.getUrl(this._dataSource);
    if (!url) {
      return this._onError(new Error('Invalid URL'));
    }
    this._requestURL = url;

    this._xhr = new XMLHttpRequest();
    this._xhr.open('GET', this._requestURL, true);

    this._xhr.setRequestHeader('Range', `bytes=${this._range.start}-${this._range.end}`);
    this._xhr.responseType = 'moz-chunked-arraybuffer';

    this._xhr.onreadystatechange = this._onReadyStateChange.bind(this);
    this._xhr.onprogress = this._onProgress.bind(this);
    this._xhr.onloadend = this._onLoadEnd.bind(this);
    this._xhr.onerror = this._onXhrError.bind(this);

    this._requestAbort = false;
    this._xhr.send();
  }

  abort() {
    if (this._xhr && this._xhr.readyState !== XMLHttpRequest.DONE) {
      this._requestAbort = true;
      this._xhr.abort();
    }
  }

  _onReadyStateChange(e) {
    if (this._xhr.readyState === XMLHttpRequest.HEADERS_RECEIVED) {
      const contentLength = this._xhr.getResponseHeader('Content-Length');
      if (contentLength) {
        this._contentLength = parseInt(contentLength, 10);
        if (this._onContentLengthKnown) {
          this._onContentLengthKnown(this._contentLength);
        }
      }

      const location = this._xhr.getResponseHeader('Location');
      if (location && this._onURLRedirect) {
        this._onURLRedirect(location);
      }
    }
  }

  _onProgress(e) {
    if (e.lengthComputable) {
      this._receivedLength += e.loaded - this._xhr._lastReceivedLength;
      this._xhr._lastReceivedLength = e.loaded;
      if (this._onDataArrival) {
        this._onDataArrival(this._xhr.responseText);
