      }
      case 'put': {
        return this.requestMethod.put(url, {
          headers: options.headers,
          data: options.data,
        });
      }
      case 'post': {
        return this.requestMethod.post(url, {
          headers: options.headers,
          data: options.data,
          requestType: options.requestType,
        });
      }
      default: {
